(()=>{class e{constructor(e,t,c){this.title=e,this.description=t,this.colorCode=c}}class t{constructor(e,t,c){this.name=e,this.description=t,this.tasks=[],this.colorCode=c}addTask(t,c,s){this.tasks.push(new e(t,c,s))}deleteTask(e){this.tasks.splice(e,1)}}let c=new class{constructor(){this.projects=[]}addProject(){const e=document.querySelector("#project-name").value,c=document.querySelector("#project-description").value,s=document.querySelector("#project-color").value;this.projects.push(new t(e,c,s)),render()}render(){document.querySelector(".project-list").innerHTML="",this.projects.forEach((e=>{const t=document.createElement("div");t.classList.add("project-card"),t.innerHTML=`\n            <div class="project-card-header">\n            <svg\n            xmlns="http://www.w3.org/2000/svg"\n            viewBox="0 0 24 24">\n            <title>circle-medium</title>\n            <pathd="M12,8A4,4 0 0,0 8,12A4,4 0 0,0 12,16A4,4 0 0,0 16,12A4,4 0 0,0 12,8Z"/>           \n            <p class="project">${e.name}</p>\n            </div>`}))}};const s=document.getElementById("myModal");document.querySelector(".close").addEventListener("click",(()=>{s.style.display="none"})),document.querySelector(".add-project").addEventListener("click",(()=>{s.style.display="block"})),document.querySelector(".submit-project").addEventListener("click",(e=>{e.preventDefault(),c.addProject()}))})();